			rtcpPadding = (((rtcpHeader[0] & 0x20) >> 5) === 1);
			rtcpCount = rtcpHeader[0] & 0x1F;
			rtcpType = rtcpHeader[1];
			rtcpLength = (rtcpHeader[2] << 8) + rtcpHeader[3];

